sourceskel.declarations:
const int relais = D1;
const int mode = $mode;
String trigger = "$triggervalue";
int state = 0;

sourceskel.functions:
float toFloat(String s){
	return s.toFloat();
}
void switchRelais(float compareValue){
if(mode==0){
	if(compareValue>toFloat(trigger)){
	digitalWrite(relais,HIGH);
	state=1;
	}else{
		digitalWrite(relais,LOW);
		state=0;
	}
}
if(mode==1){
	if(compareValue<toFloat(trigger)){
		digitalWrite(relais,HIGH);
		state=1;
		}else{
			digitalWrite(relais,LOW);
			state=0;
		}
	}
}
