const char* wificlienthostname   = "$wificlienthostname";
ESP8266WebServer server(80);

boolean configure = false;

sourceskel.functions:
void writeWifiConfig(String ssid, String password) {
	String base = "{\"ssid\":\"WIFINAME\",\"password\": \"KEY\"}";
	SPIFFS.begin();

	File f = SPIFFS.open("/wificonf.json", "w");
	if (!f) {
		Serial.println("file open failed");
	}
	Serial.println("====== Writing to SPIFFS file =========");
	// write 10 strings to file
	base.replace("WIFINAME", ssid);
	base.replace("KEY", password);
	Serial.println(base);

	f.println(base);

	f.close();
	SPIFFS.end();

}


void readWifiConfigAndConnect() {
	StaticJsonBuffer<1024> jsonBuffer;
	Serial.println("");
	SPIFFS.begin();
	// read SPIFFS
	File f = SPIFFS.open("/wificonf.json", "r");
	delay(10);
	if (!f) {
		Serial.println("file open failed");
		/*configure=true;
		wifiConfigMode();
		return;*/

	}


	String s = f.readString();
	f.close();
	SPIFFS.end();
	Serial.println(s);

	JsonObject& root = jsonBuffer.parseObject(s);
	if (!root.success()) {
		Serial.println("parseObject() failed");
		wifiConfigMode();
		return;
	}
	const char* ssid = root["main"];
	const char* password = root["password"];
	Serial.println("\nConfig:");

	Serial.println(ssid);
	Serial.println(password);



	delay(10);
	// We start by connecting to a WiFi network
	Serial.println();
	Serial.print("Connecting to ");
	Serial.println(ssid);
	WiFi.mode(WIFI_STA);
	WiFi.disconnect();
	WiFi.begin(ssid, password);
	int counter = 0;
	while (WiFi.status() != WL_CONNECTED) {
		delay(500);
		Serial.print(".");
		counter++;
		if (counter == 120) {
			counter = 0;
			Serial.print("Configuring access point...");
			/* You can remove the password parameter if you want the AP to be open. */
			configure = true;
			Serial.println("");
			Serial.println("WiFi configuration mode");
			wifiConfigMode();

		}
		if (configure) break;
	}
	if (configure == false) {
		Serial.println("");
		Serial.println("WiFi connected");
		Serial.println("IP address: ");
		Serial.println(WiFi.localIP());
	}
}

DNSServer dnsServer;
const byte DNS_PORT = 53;


void wifiConfigMode() {
	IPAddress ip(192, 168, 1, 1);
	IPAddress NMask(255, 255, 255, 0);
	WiFi.mode(WIFI_AP);
	WiFi.softAPConfig(ip, ip, NMask);
	WiFi.softAP("esp8266Conf", "esp8266Conf");

	dnsServer.setErrorReplyCode(DNSReplyCode::NoError);
	dnsServer.start(DNS_PORT, "*", ip);



	server.on("/", handleRoot);
	server.on("/generate_204", handleRoot); //Android captive portal. Maybe not needed. Might be handled by notFound handler.
	server.onNotFound([]() {
		server.send(200, "text/html",	"<form class=\"form-signin\" action=\"http://192.168.1.1/\" method=\"get\">	<h2 class=\"form-signin-heading\">WiFi Conf:</h2>	<label for=\"Zeile1\" class=\"sr-only\"></label>	<input id=\"Zeile1\" name=\"ssid\" maxlength=\"20\" class=\"form-control\" placeholder=\"ssid\" autofocus=\"\" type=\"text\"> </br>	<label for=\"Zeile2\" class=\"sr-only\"></label>	<input id=\"Zeile2\" name=\"password\" maxlength=\"20\" class=\"form-control\" placeholder=\"password\" type=\"text\"> <br/>	<button class=\"btn btn-lg btn-primary btn-block\" type=\"submit\">submit</button>	</form>");
	});
	server.begin();

	// replay to all requests with same HTML


	Serial.println("Server Started");
}

void handleRoot() {

	// get and save data from http request
	String ssid = server.arg("ssid"); //this lets you access the value using the key as you have set in your json for ex:json={'body':'foo'}
	String password = server.arg("password"); //this lets you access the value using the key as you have set in your json for ex:json={'body':'foo'}

	if (ssid.length() > 0 && password.length() > 0) {
		Serial.println(ssid);
		Serial.println(password);

		// send reply to client
		writeWifiConfig(ssid, password);
		server.send(200, "text/html", "Done! Der Accesspoint wird jetzt geschlossenn..");

		// disable wifi
		WiFi.mode(WIFI_OFF);
		configure = false;
		readWifiConfigAndConnect();
		server.stop();
		dnsServer.stop();
	} else

		server.send(200, "text/html",	"<form class=\"form-signin\" action=\"http://192.168.1.1/\" method=\"get\">	<h2 class=\"form-signin-heading\">WiFi Conf:</h2>	<label for=\"Zeile1\" class=\"sr-only\"></label>	<input id=\"Zeile1\" name=\"ssid\" maxlength=\"20\" class=\"form-control\" placeholder=\"ssid\" autofocus=\"\" type=\"text\"> </br>	<label for=\"Zeile2\" class=\"sr-only\"></label>	<input id=\"Zeile2\" name=\"password\" maxlength=\"20\" class=\"form-control\" placeholder=\"password\" type=\"text\"> <br/>	<button class=\"btn btn-lg btn-primary btn-block\" type=\"submit\">submit</button>	</form>");
}
